  // src/utils.js
  var nl2br = (str) => {
    return str.replace(/\n/g, "<br />\n");
  };

  // src/menu.js
  var menudata = [
    ["ON/OFF", "", null],
    ["Architects", "list:architects", null],
    [
      "Categories",
      "categories",
      [
        ["New Commons", "cat:new-commons", null],
        ["Building Culture", "cat:building-culture", null],
        ["Urban Development", "cat:urban-development", null],
        ["Sustainable Architecture", "cat:sustainable-architecture", null],
        ["Landscapes in Transition", "cat:landscapes-in-transition", null],
        ["Cultural Heritage", "cat:cultural-heritage", null],
        ["Extreme Architecture", "cat:extreme-architecture", null]
      ]
    ],
    ["Search", "search", null],
    [
      "About",
      "about",
      [
        ["Rector\u2019s Speech", "page:rectors-speech", null],
        ["Curators voice", "page:curators-voice", null],
        ["Prizes", "page:prizes", null],
        ["Credits", "page:credits", null],
        ["Contact", "page:contact", null]
      ]
    ]
  ];
  var selector = ".menu";
  var clearSelection = () => {
    console.log("clearSelection");
    document.querySelectorAll(selector + " .selected").forEach((el) => el.classList.remove("selected"));
  };
  var collapseAll = () => {
    console.log("collapseAll");
    document.querySelectorAll(selector + " .grpc.open").forEach((el) => el.classList.remove("open"));
  };
  var buildMenu = () => {
    let html = "";
    menudata.forEach((m) => {
      if (m[2] != null) {
        html += `<div data-menu-action='toggle' data-menu-key='${m[1]}'>${m[0]}</div><div class="grpc" id='${m[1]}'>`;
        m[2].forEach((sm) => {
          html += `<div class='sml' data-menu-action='go' data-menu-key='${sm[1]}'><span>${sm[0]}</span></div>`;
        });
        html += `</div>`;
      } else {
        html += `<div data-menu-action='go' data-menu-key='${m[1]}'>${m[0]}</div>`;
      }
    });
    document.querySelector(selector).innerHTML = html;
    document.querySelectorAll(selector + ` [data-menu-action="go"]`).forEach((el) => {
      el.addEventListener("click", () => {
        document.querySelectorAll(selector + " .selected").forEach((el2) => el2.classList.remove("selected"));
        el.classList.add("selected");
        if (el.parentElement.classList.contains("grpc")) {
          el.parentElement.previousElementSibling.classList.add("selected");
        }
        if (!el.classList.contains("sml")) {
          document.querySelectorAll(selector + " .grpc.open").forEach((el2) => el2.classList.remove("open"));
        }
        window.location.hash = `#${el.getAttribute("data-menu-key")}`;
      });
    });
    document.querySelectorAll(selector + ` [data-menu-action="toggle"]`).forEach((el) => {
      el.addEventListener("click", () => {
        if (el.classList.contains("selected")) {
          el.classList.remove("selected");
          document.querySelector(`#${el.getAttribute("data-menu-key")}`).classList.remove("open");
          window.location.hash = "";
          return;
        } else {
          document.querySelectorAll(selector + " .selected").forEach((el2) => el2.classList.remove("selected"));
          el.classList.add("selected");
          document.querySelectorAll(selector + " .grpc.open").forEach((el2) => el2.classList.remove("open"));
          document.querySelector(`#${el.getAttribute("data-menu-key")}`).classList.add("open");
        }
      });
    });
  };

  // src/layout-offgrid.js
  var cleanupOffgrid = (selector2) => {
    document.querySelectorAll(`${selector2} .offgrid-spacer`).forEach((s) => s.remove());
    const els = document.querySelectorAll(`${selector2} .project`);
    els.forEach((el, i) => {
      el.style.marginRight = "";
    });
  };
  var renderOffgrid = (selector2, data) => {
    console.log("renderOffgrid");
    const root = document.querySelector(`${selector2}`);
    const els = document.querySelectorAll(`${selector2} .project`);
    els.forEach((el, i) => {
      const MAX = window.innerWidth / 4;
      el.style.marginRight = `${10 + Math.round(Math.random() * MAX)}px`;
    });
    els.forEach((el, i) => {
      const MAX = i === 0 ? 100 : 400;
      const rh = Math.round(Math.random() * Math.random() * MAX);
      const n = document.createElement("div");
      n.classList = "offgrid-spacer";
      n.style.height = `${rh}px`;
      el.insertBefore(n, el.firstChild);
    });
  };

  // src/modes.js
  var modeToClass = () => {
    let className = "";
    if (MODE.onoff === false && MODE.gridline === false) {
      className = "offline";
    }
    if (MODE.onoff === true && MODE.gridline === false) {
      className = "online";
    }
    if (MODE.onoff === true && MODE.gridline === true) {
      className = "ongrid";
    }
    if (MODE.onoff === false && MODE.gridline === true) {
      className = "offgrid";
    }
    return className;
  };
  var classToMode = (className) => {
    if (className === "offline") {
      MODE.onoff = false;
      MODE.gridline = false;
    }
    if (className === "online") {
      MODE.onoff = true;
      MODE.gridline = false;
    }
    if (className === "ongrid") {
      MODE.onoff = true;
      MODE.gridline = true;
    }
    if (className === "offgrid") {
      MODE.onoff = false;
      MODE.gridline = true;
    }
  };
  var MODES = ["ongrid", "offgrid", "online", "offline"];
  var nextMode = () => {
    const curr = MODES.indexOf(modeToClass());
    const next = (curr + 1) % MODES.length;
    classToMode(MODES[next]);
  };

  // src/index.js
